mapper.insert:
		INSERT INTO MEMBER (
			   MEM_ID           
             , MEM_PASS          
             , MEM_EMAIL
             , MEM_PHONE         
		) VALUES (
			  #{memId}
			, #{memPass}
			, #{memEmail}
			, #{memPhone}
		)

mapper.update:
UPDATE MEMBER
SET    MEM_PASS = #{memPass}
	  ,MEM_EMAIL = #{memEmail}
	  ,MEM_PHONE = #{memPhone}
WHERE  MEM_ID = #{memId}
